using System;
using System.Collections.Generic;
using System.Text;
using Utils.NET.IO;
using Utils.NET.Logging;

namespace Utils.NET.Net.Udp.Packets
{
    public class UdpConnect : UdpPacket
    {
        public override UdpPacketType Type => UdpPacketType.Connect;

        /// <summary>
        /// Salt generated by the client and sent to the server
        /// </summary>
        public ulong clientSalt;

        public UdpConnect() { }

        public UdpConnect(ulong clientSalt) => this.clientSalt = clientSalt;

        protected override void Read(BitReader r)
        {
            clientSalt = r.ReadUInt64();
        }

        protected override void Write(BitWriter w)
        {
            w.Write(clientSalt);
        }
    }
}
